const { Client, Intents, SlashCommandBuilder } = require('discord.js');

const client = new Client({ intents: [Intents.FLAGS.GUILDS, Intents.FLAGS.GUILD_MEMBERS] });

client.on('ready', () => console.log(`Logged in as ${client.user.tag}!`));

// Define the /ban slash command
const banCommand = new SlashCommandBuilder()
 .setName('ban')
 .setDescription('Bans a user from the server.')
 .addUserOption(option =>
    option.setName('user')
     .setDescription('The user to ban')
     .setRequired(true)
  )
 .addStringOption(option =>
    option.setName('reason')
     .setDescription('Reason for the ban (optional)')
     .setRequired(false)
  );

client.on('interactionCreate', async interaction => {
  if (!interaction.isCommand()) return;

  if (interaction.commandName === 'ban') {
    try {
      const user = interaction.options.getUser('user');
      const reason = interaction.options.getString('reason') || 'No reason provided';

      await interaction.guild.members.ban(user, { reason });
      await interaction.reply({ content: `Successfully banned ${user.tag} for ${reason}`, ephemeral: true });
    } catch (error) {
      console.error(error);
      await interaction.reply({ content: 'An error occurred while banning the user.', ephemeral: true });
    }
  }
});

client.login('YOUR_BOT_TOKEN'); 
```

