using namespace std;

int main() {
    string filename, wordToCount;
    int count = 0;

    cout << "Enter filename: ";
    cin >> filename;
    cout << "Enter word to count: ";
    cin >> wordToCount;

    ifstream file(filename);
    if (!file.is_open()) {
        cerr << "Error opening file: " << filename << endl;
        return 1;
    }

    unordered_map<string, int> wordCounts; // Store word counts efficiently

    string line;
    while (getline(file, line)) {
        stringstream ss(line);
        string word;
        while (ss >> word) {
            if (word == wordToCount) {
                wordCounts[word]++; 
            }
        }
    }

    count = wordCounts[wordToCount];
    file.close();

    cout << "Word '" << wordToCount << "' appears " << count << " times in the file." << endl;

    return 0;
}
```

